import React, { useState } from 'react';
import Navbar2 from '../components/Navbar2';

function AddRecipeForm() {

    const [name, setName] = useState('');
    const [ingredientsText, setIngredientsText] = useState([]);
    const [stepsText, setStepsText] = useState([]);
    const [price, setPrice] = useState('');
    const [type, setType] = useState('');

    const [ingredientsCount, setIngredientsCount] = useState(1);

    const [ingredients, setIngredients] = useState([<textarea key={ingredientsCount} className="input-style" rows="4" cols="50"/>]);

    const addIngredient = () => {  
        setIngredientsCount(ingredientsCount + 1);
        setIngredients([...ingredients, <textarea key={ingredientsCount + 1} className="input-style" rows="4" cols="50"/>]);
    }

    const [stepsCount, setStepsCount] = useState(1);

    const [steps, setSteps] = useState([<textarea key={ingredientsCount} className="input-style" rows="4" cols="50"/>]);

    const addStep = () => {
        setStepsCount(stepsCount + 1);
        setSteps([...steps, <textarea key={ingredientsCount} className="input-style" rows="4" cols="50"/>]);
    }



    return (
        <div id="add-recipe-form">
            <Navbar2 willContainSearchBar={true}/>
            <h3>Recipe Creation</h3>
            <div id="form">
                <label>
                    Name:
                    <input className="input-style" type="text" value={name} onChange={(event) => setName(event.target.value)}/>
                </label>
                <label>
                    Ingredients:
                    {ingredients}
                </label>
                <div className="adding-text-area icon-hover" onClick={()=> addIngredient()}></div>
                <label>
                    Steps:
                    {steps}
                    {/* <textarea className="input-style" rows="4" cols="50" value={stepsText} onChange={(event) => setStepsText(stepsText.push(event.target.value))}/> */}
                </label>
                <div className="adding-text-area icon-hover" onClick={()=> addIngredient()}></div>
                <label>
                    Estimated Price:
                    <input className="input-style" type="text" value={price} onChange={(event) => setPrice(event.target.value)}/>
                </label>
                <label>
                    Type:
                    <input className="input-style" type="text" value={type} onChange={(event) => setType(event.target.value)}/>
                </label>
                <button onClick={() => addRecipe()}>Add Comment</button>
            </div>
        </div>
    )
}

export default AddRecipeForm;
