import './App.css';
import {useState} from 'react';
import MainPage from './pages/MainPage';
import AboutPage from './pages/AboutPage';
import HomePage from './pages/HomePage';
import Recipe from './pages/Recipe';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';

function App() {
  const [loginStatus, setLoginStatus] = useState(false);
  return (
    <Router>
      <div className="App">
          <Switch> {/** Switch makes it so that only the first rout that matches gets rendered. */}

            <Route path="/"
            render={() => (
              <MainPage loginStatus={loginStatus} setLoginStatus={setLoginStatus}/>
            )} 
            exact
            />

            <Route path="/About"
            render={() => (
              <AboutPage loginStatus={loginStatus} setLoginStatus={setLoginStatus}/>
            )} 
            />

            <Route path="/Home"
            render={() => (
              <HomePage loginStatus={loginStatus} setLoginStatus={setLoginStatus}/>
            )} 
            />
            <Route path="/Recipe/:name" component={Recipe} />
          </Switch>
      </div>
    </Router>
  );
}

export default App;
