import React, { useState, useEffect } from 'react';
import Navbar2 from '../components/Navbar2';
import RecomendationCard from '../components/RecomendationCard';
import FoodCards from '../components/FoodCards';
import Eggs from '../imgs/eggs.jpeg';
import Footer2 from '../components/Footer2';

function HomePage() {
    const [count, setCount] = useState(0);
    const search = 'food';

    const [recipes, setRecipeInfo] = useState([]);

    let allRecipe = {};

    useEffect(() => {
        const fetchData = async () => {
            const result = await fetch('/api/recipes');
            const body = await result.json();
            allRecipe = body;
            console.log(allRecipe);
            setRecipeInfo(body);
            setCount(body.length);
        }
        fetchData();
    }, []);
    console.log(allRecipe);

    const [listCards, setListCards] = useState(<div></div>);

    useEffect(() => {
        const cards = recipes.map((recipe) =>
                <FoodCards key={recipe._id} foodName={recipe.name} estimatedPrice={recipe.estimatedPrice}  ingredients={recipe.ingredients} steps={recipe.steps}/>
        );
        setListCards(cards);


    }, [recipes]);


    return (
        <div id="homepage">
            <Navbar2 allRecipes={allRecipe}/>
            <div id="search-info">
                <h5>{count} found for "{search}"</h5>
                <p>See Also:</p>
            </div>
            <div id="extra-eating-options">
                <RecomendationCard recomendation="Dinner" setRecipeInfo={setRecipeInfo}/>
                <RecomendationCard recomendation="Lunch" setRecipeInfo={setRecipeInfo}/>
                <RecomendationCard recomendation="Breakfast" setRecipeInfo={setRecipeInfo}/>
                <RecomendationCard recomendation="Dessert" setRecipeInfo={setRecipeInfo}/>
                <RecomendationCard recomendation="Snacks" setRecipeInfo={setRecipeInfo}/>
                <RecomendationCard recomendation="Drinks" setRecipeInfo={setRecipeInfo}/>
            </div>
            <div id="food-cards-container">
                {listCards}
            </div>
            <Footer2/>
        </div>
    );
}

export default HomePage;
