import React from 'react';
import {Link} from 'react-router-dom';

function FoodCards({foodName, estimatedPrice, image, ingredients, steps}) {

    //For the ingredients
    let listedIngredients = "";
    for (let num = 0; num < ingredients.length; num++) {
        if (num < (ingredients.length - 1)) {
            listedIngredients += (ingredients[num] + ", ");
        } else {
            listedIngredients += ingredients[num];
        }
    };

    return (

        <div id="food-cards">
            <div id="flip-card">
                <div id="card-front">
                    <div id="food-cards-name">
                        <p>{foodName}</p>
                    </div>
                    <div id="food-cards-image">
                        <img src={image} alt="recipe pic"></img>
                    </div>
                    <div id="food-cards-price">
                        <p>{estimatedPrice}</p>
                    </div>
                </div>

                <div id="card-back">
                    <div>
                        <h5>Ingredients</h5>
                        <p>{listedIngredients}</p>
                    </div>
                    <div>
                        <h5>Steps</h5>
                        <p>{steps}</p>
                    </div>
                    <Link to={{
                        pathname: `/Recipe/${foodName}`,
                        aboutProps: {
                            foodName: foodName,
                            estimatedPrice: estimatedPrice,
                            image: image,
                            ingredients: ingredients,
                            steps: steps
                        }
                    }}> View </Link>
                </div>
            </div>
        </div>
    );
}

export default FoodCards;
