import React, { useState, useEffect} from 'react';
import Navbar2 from '../components/Navbar2';

function AddRecipeForm( props ) {

    const [finishedRecipe, setFinishedRecipe] = useState(false);

    const [name, setName] = useState('');
    const [ingredientsText, setIngredientsText] = useState([]);
    const [stepsText, setStepsText] = useState([]);
    const [price, setPrice] = useState(0);
    const [type, setType] = useState('');

    const [ingredientsCount, setIngredientsCount] = useState(1);
    const [ingredients, setIngredients] = useState([<textarea key={ingredientsCount} className="input-style" rows="4" cols="50"/>]);
    const addIngredient = () => {  
        setIngredients([...ingredients, <textarea key={ingredientsCount + 1} className="input-style" rows="4" cols="50"/>]);
        setIngredientsCount(ingredientsCount + 1);
        console.log(ingredientsText)
    }

    const [stepsCount, setStepsCount] = useState(1);
    const [steps, setSteps] = useState([<textarea key={stepsCount} className="input-style" rows="4" cols="50"/>]);
    const addStep = () => {
        setSteps([...steps, <textarea key={stepsCount + 1} className="input-style" rows="4" cols="50"/>]);
        setStepsCount(stepsCount + 1);
    }

    const addRecipe = async() => {
            const result = await fetch(`/api/profile/post`, {
                method: 'post',
                body: JSON.stringify({name, estimatedPrice: price, ingredients: ingredientsText, steps: stepsText, eatingTime: type}),
                headers: {
                    'Content-Type': 'application/json',
                }
            });
            const body = await result.json();
            props.location.aboutProps.setRecipeInfo(body);
            setName("");
            setIngredientsText([]);
            setStepsText([]);
            setPrice(0);
            setType("");
            console.log(finishedRecipe)
            setFinishedRecipe(true);
            console.log(finishedRecipe)
    }

    useEffect(() => {
        setFinishedRecipe(true);

    }, [finishedRecipe])

    console.log(finishedRecipe)
    if (finishedRecipe) {

        return (
            <div id="add-recipe-form">
                <Navbar2 willContainSearchBar={true}/>
                <h3>Recipe Creation</h3>
                <p>Finished</p>
            </div>
        )

    } else {

        return (
            <div id="add-recipe-form">
                <Navbar2 willContainSearchBar={true}/>
                <h3>Recipe Creation</h3>
                <div id="form">
                    <label>
                        Name:
                        <input className="input-style" type="text" value={name} onChange={(event) => setName(event.target.value)}/>
                    </label>
                    <label>
                        Ingredients:
                        {ingredients}
                    </label>
                    <div className="adding-text-area icon-hover" onClick={()=> addIngredient()}></div>
                    <label>
                        Steps:
                        {steps}
                        {/* <textarea className="input-style" rows="4" cols="50" value={stepsText} onChange={(event) => setStepsText(stepsText.push(event.target.value))}/> */}
                    </label>
                    <div className="adding-text-area icon-hover" onClick={()=> addStep()}></div>
                    <label>
                        Estimated Price:
                        <input className="input-style" type="text" value={price} onChange={(event) => setPrice(event.target.value)}/>
                    </label>
                    <label>
                        Type:
                        <select className="input-style" type="text" value={type} onChange={(event) => setType(event.target.value)}>
                            <option>Lunch</option>
                            <option>Dinner</option>
                            <option>Breakfast</option>
                            <option>Dessert</option>
                            <option>Snacks</option>
                            <option>Drinks</option>
                        </select>
                    </label>
                    <button onClick={addRecipe}>Add Comment</button>
                </div>
            </div>
        )
    }
}

export default AddRecipeForm;
