import React from 'react';
import Navbar2 from '../components/Navbar2';
import Footer2 from '../components/Footer2';

function Recipe( props ) {

    let listedIngredients = "";
    const ing = props.location.aboutProps.ingredients;
    for (let num = 0; num < ing; num++) {
        if (num < (ingredients.length - 1)) {
            listedIngredients += (ingredients[num] + ", ");
        } else {
            listedIngredients += ingredients[num];
        }
    };

    const listedSteps = props.location.aboutProps.steps.map((step) =>
        <>
            <p>{step}</p>
        </>
    );

    return (
        <div id="recipe-page">
            <Navbar2/>
            <div id="recipe-info">
                <img src={props.location.aboutProps.image} alt="recipe pic"></img>
                <div>
                    <h5>Name:</h5>
                    <p>{props.location.aboutProps.foodName}</p>
                    <h5>Ingredients:</h5>
                    <p>{props.location.aboutProps.ingredients}</p>
                    <h5>Steps:</h5>
                    {listedSteps}
                    <h5>Estimated Price:</h5>
                    <p>{props.location.aboutProps.estimatedPrice}</p>
                </div>
                <Footer2/>
            </div>

        </div>
    );
}

export default Recipe;
