import React, { useState, useEffect } from 'react';
import {Link} from 'react-router-dom';


function Navbar2(allRecipes) {

    const [suggestions, setSuggestions] = useState([]);

    const onTextChanged = (e) => {
        const value = e.target.value;
        let suggestions = [];
        if (value.length > 0) {
            const regex = new RegExp(`^${value}`, 'i');
            suggestions = allRecipes.sort().filter(v => regex.test(v));
        }
        setSuggestions(suggestions);
    };
    const renderSuggestions = () => {
        if (suggestions.length === 0) {
            return null;
        }
        return (
            <ul>
                {allRecipes.map((recipes) => <li>{recipes}</li>)}
            </ul>
        )
    };

    return (
        <div id="navbar2">
            <h1>College Kitchen</h1>

            <div id="search-bar">
                <input onChange={onTextChanged} type="text" placeholder="Search.."></input>
                {renderSuggestions}
                <button type="submit"></button>
            </div>

            <div id="home-chef-container">
                <Link id="home" to="/Home" className="icon-hover"> </Link>
                <Link id="chef" to="/Profile" className="icon-hover"> </Link>
            </div>
        </div>
    );
}

export default Navbar2;
