import React, { useState, useEffect } from 'react';
import {Link} from 'react-router-dom';


function Navbar2(props) {

    const [allRecipes, setAllRecipes] = useState(props);

    const [allRecipe, setAllRecipe] = useState([]);

    useEffect (() => {
        setAllRecipes(props);
        let allRec = [];
        Object.entries(allRecipes).map(([key, value]) => {
            allRec.push(value.name)
        })
        setAllRecipe(allRec);
    }, [props]);

    const [suggestions, setSuggestions] = useState([]);


    const onTextChanged = (e) => {
        const value = e.target.value;
        let suggestions = [];
        if (value.length > 0) {
            const regex = new RegExp(`^${value}`, 'i');
            suggestions = allRecipe.sort().filter(v => regex.test(v));
        }
        setSuggestions(suggestions);

    };


    const [renderSuggestions, setRenderSuggestions] = useState(<option></option>);

    useEffect (() => {
        const renderSuggestion = () => {
            if (suggestions.length === 0) {
                return null;
            }
            return (
                suggestions.map((recipes, key) => <option key={key} className="dropdown-options" value={recipes}>{recipes}</option>)
            )
        };
        setRenderSuggestions(renderSuggestion);
    }, [suggestions]);


    return (
        <div id="navbar2">
            <h1>College Kitchen</h1>

            <div id="search-bar">
                <div>
                    <input onChange={onTextChanged} type="text" placeholder="Search.."></input>
                    <div id="search-dropdown">
                        {renderSuggestions}
                    </div>
                </div>
                <button type="submit"></button>
            </div>

            <div id="home-chef-container">
                <Link id="home" to="/Home" className="icon-hover"> </Link>
                <Link id="chef" to="/Profile" className="icon-hover"> </Link>
            </div>
        </div>
    );
}

export default Navbar2;
