import React, { useState, useEffect } from 'react'
import Navbar2 from '../components/Navbar2';

function AddRecipeForm() {

    const [name, setName] = useState('');
    const [ingredientsText, setIngredientsText] = useState([]);
    const [stepsText, setStepsText] = useState([]);
    const [price, setPrice] = useState('');
    const [type, setType] = useState('');

    const [Ingredients, setIngredients] = useState([<textarea className="input-style" rows="4" cols="50" value={ingredientsText} onChange={(event) => setIngredientsText(ingredientsText.push(event.target.value))}/>]);

    useEffect(() => {
        let cards;
        if (recipes.length < 1) {
            cards = <p>No Results</p>;
        } else {
            cards = recipes.map((recipe) =>
                <FoodCards key={recipe._id} foodName={recipe.name} estimatedPrice={recipe.estimatedPrice}  ingredients={recipe.ingredients} steps={recipe.steps}/>
            );
        }
        setListCards(cards);


    }, [Ingredients]);

    const addRecipe = () => {

    }

    const addIngredient = () => {  
        let ingr = Ingredients;
        ingr.push(<textarea className="input-style" rows="4" cols="50" value={ingredientsText} onChange={(event) => setIngredientsText(ingredientsText.push(event.target.value))}/>);
        setIngredients(ingr);
    }



    return (
        <div id="add-recipe-form">
            <Navbar2 willContainSearchBar={true}/>
            <h3>Recipe Creation</h3>
            <div id="form">
                <label>
                    Name:
                    <input className="input-style" type="text" value={name} onChange={(event) => setName(event.target.value)}/>
                </label>
                <label>
                    Ingredients:
                    {}
                </label>
                <div className="adding-text-area icon-hover" onClick={addIngredient}></div>
                <label>
                    Steps:
                    <textarea className="input-style" rows="4" cols="50" value={stepsText} onChange={(event) => setStepsText(stepsText.push(event.target.value))}/>
                </label>
                <div className="adding-text-area icon-hover"></div>
                <label>
                    Estimated Price:
                    <input className="input-style" type="text" value={price} onChange={(event) => setPrice(event.target.value)}/>
                </label>
                <label>
                    Type:
                    <input className="input-style" type="text" value={type} onChange={(event) => setType(event.target.value)}/>
                </label>
                <button onClick={() => addRecipe()}>Add Comment</button>
            </div>
        </div>
    )
}

export default AddRecipeForm;
